    public void chamaDialog(int indice) {
        String msg = "";

        switch (indice) {
            case 0:
                msg = "Status Emergencial é: VERMELHO, seu atendimento será imediato!";
                break;
            case 1:
                msg = "Status Emergencial é : AMARELO, aguarde 60 minutos.";
                break;
            case 2:
                msg = "Status Emergencial é: VERMELHO, seu atendimento será imediato!";
                break;
            default:
                break;
            case 3:
                msg = "Status Emergencial é : VERDE, aguarde até 120 minutos.";
                break;

        }

        Dialog dialog = new Dialog();
        dialog.setTitle("Status Emergencial");
        ButtonType type = new ButtonType("Ok", ButtonData.OK_DONE);
        //Setting the content of the dialog
        dialog.setContentText(msg);
        //Adding buttons to the dialog pane
        dialog.getDialogPane().getButtonTypes().add(type);
        dialog.showAndWait();
    }

    public SVGPath retNewSVGPath(int indice) {
        SVGPath svgPath;

        List<String> listPath = addSVGPath();

        svgPath = new SVGPath();
        svgPath.setContent(listPath.get(indice));
        svgPath.setFill(Color.BROWN);
        svgPath.setScaleX(0.5);
        svgPath.setScaleY(0.5);
        svgPath.setOpacity(1.0);
        svgPath.setStrokeWidth(2);
        svgPath.setStroke(Color.BLACK);

        svgPath.setOnMouseClicked((event) -> {
            WebView webVideo = new WebView();

            if (pane.getChildren().contains(webVideo)) {
                pane.getChildren().remove(webVideo);
            }

            //Recupera link video 
            List<String> lista = addLinkVideo(indice);

            webVideo.getEngine().loadContent(lista.get(0), "text/html");
            webVideo.setTranslateX(0);
            webVideo.setTranslateY(250);
            pane.getChildren().add(webVideo);

            chamaDialog(indice);
            
            System.out.println("Clicou");
        });

        return svgPath;
    }

