    @Override
    public void start(Stage stage) throws Exception {
        SVGPath pulmao;
        SVGPath rins;
        SVGPath figado;
        SVGPath estomago;
        /*SVGPath pancreas;
        SVGPath timo;*/

        pulmao = retNewSVGPath(0);
        rins = retNewSVGPath(1);
        figado = retNewSVGPath(2);
        estomago = retNewSVGPath(3);
        /*pancreas = retNewSVGPath(4);
        timo = retNewSVGPath(5);*/

        HBox hbox = new HBox();
        hbox.setSpacing(5);
        hbox.setAlignment(Pos.TOP_LEFT);
        //hbox.setPadding(new javafx.geometry.Insets(20, 20, 20, 20));
        hbox.setPrefHeight(150);
        hbox.setPrefWidth(150);
        hbox.setMaxHeight(150);
        hbox.setMaxWidth(150);

        hbox.getChildren().addAll(pulmao, rins, figado, estomago);

        pane = new Pane();
        pane.getChildren().add(hbox);

        Scene cena = new Scene(pane, 800, 600);
        stage.setTitle("Sintoma na área:");
        stage.setScene(cena);
        stage.setResizable(false);
        stage.show();
    }

    public static void main(String[] args) {
        launch(args);
    }
