    @Override
    public void start(Stage plano) throws Exception {
        SVGPath corpo;

        corpo = new SVGPath();
        corpo.setContent(svgPathCorpo);

        //corpo.setFill(Color.BROWN); // experimente cavalo.setFill(Color.WHITE); 
        corpo.setScaleX(1.0);     // experimente ajustar a escala em X
        corpo.setScaleY(1.0);     // experimente ajustar a escala em Y
        corpo.setOpacity(1.0); 
        corpo.setStrokeWidth(10);
        corpo.setStroke(Color.BLACK);

        StackPane painel = new StackPane();
        painel.getChildren().add(corpo);
        Scene cena = new Scene(painel, 600, 450);
        plano.setTitle("Sintoma na √Årea:");
        plano.setScene(cena);
        plano.show();
    }
